package com.example.test_sportpro.ui.fragments.trainerProfileimport android.os.Bundleimport android.util.Logimport androidx.fragment.app.Fragmentimport android.view.Viewimport androidx.navigation.fragment.findNavControllerimport androidx.navigation.fragment.navArgsimport com.example.test_sportpro.Rimport com.example.test_sportpro.databinding.FragmentDetailCompetitionBindingclass DetailCompetitionFragment : Fragment(R.layout.fragment_detail_competition) {    private var fragmentDetailCompititionBinding: FragmentDetailCompetitionBinding? = null    val args: DetailCompetitionFragmentArgs by navArgs()    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        fragmentDetailCompititionBinding = FragmentDetailCompetitionBinding.bind(view)        Log.d("Args", args.event.toString())        fragmentDetailCompititionBinding!!.btnApplyCompetition.setOnClickListener {            findNavController().navigate(R.id.action_detailCompititionFragment_to_categoriesFragment)        }    }}