package com.example.test_sportpro.ui.fragments.trainerProfileimport android.os.Buildimport android.os.Bundleimport android.util.Logimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Toastimport androidx.annotation.RequiresApiimport androidx.navigation.fragment.findNavControllerimport androidx.navigation.fragment.navArgsimport com.bumptech.glide.Glideimport com.example.test_sportpro.Rimport com.example.test_sportpro.databinding.FragmentArticleBindingimport com.example.test_sportpro.databinding.FragmentDetailCompititionBindingimport com.example.test_sportpro.ui.fragments.ArticleFragmentArgsclass DetailCompititionFragment : Fragment(R.layout.fragment_detail_compitition) {    private var fragmentDetailCompititionBinding: FragmentDetailCompititionBinding? = null    val args: DetailCompititionFragmentArgs by navArgs()    @RequiresApi(Build.VERSION_CODES.O)    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        fragmentDetailCompititionBinding = FragmentDetailCompititionBinding.bind(view)        val event = args.event        fragmentDetailCompititionBinding!!.tvAddress.text = event.location        fragmentDetailCompititionBinding!!.tvPhoneNumber.text = event.creator.phone//        fragmentDetailCompititionBinding!!.backButton.setOnClickListener {}//            activity?.onBackPressed()        Log.d("Args", args.event.toString())        fragmentDetailCompititionBinding!!.btnApplyCompetition.setOnClickListener {            findNavController().navigate(R.id.action_detailCompititionFragment_to_categoriesFragment)        }    }}